package ch.hslu.informatik.prg.block2.warmup;

public class warmup_04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a = 2;
		double b = 5;
		double x = b / a;
		double y = a / b;
		
		System.out.println(b + " / " + a + " = " + x);
		System.out.println(a + " / " + b + " = " + y);
		
	}

}
