package ch.hslu.informatik.prg.block4.afg;

public class aufgabe_04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10; int b = a;
		// Ausgabe von a und b
		System.out.println("a = " + a + ", b = " + b);
		b = 20;
		// Ausgabe von a und b
		System.out.println("a = " + a + ", b = " + b);
	}

}
